cat <<'SCRIPT' | python3
import subprocess
import json
import sys

proc = subprocess.Popen(
    ['uvx', 'regen-python-mcp'],
    stdin=subprocess.PIPE,
    stdout=subprocess.PIPE,
    stderr=subprocess.PIPE,
    env={"PATH": "/home/ygg/.local/bin:/usr/local/bin:/usr/bin:/bin", "HOME": "/home/ygg", "REGEN_MCP_LOG_LEVEL": "WARNING"}
)

def send_rpc(proc, method, params=None, id_num=1):
    msg = {"jsonrpc": "2.0", "method": method, "id": id_num}
    if params is not None:
        msg["params"] = params
    data = json.dumps(msg) + "\n"
    proc.stdin.write(data.encode())
    proc.stdin.flush()
    line = proc.stdout.readline().decode().strip()
    return json.loads(line) if line else None

# Initialize
send_rpc(proc, "initialize", {
    "protocolVersion": "2024-11-05",
    "capabilities": {},
    "clientInfo": {"name": "test", "version": "1.0"}
}, 1)
notif = {"jsonrpc": "2.0", "method": "notifications/initialized"}
proc.stdin.write((json.dumps(notif) + "\n").encode())
proc.stdin.flush()

# Call list_credit_batches
result = send_rpc(proc, "tools/call", {
    "name": "list_credit_batches",
    "arguments": {"limit": 100, "count_total": True, "reverse": True}
}, 2)
print("=" * 80)
print("QUERY 1: list_credit_batches (limit=100, reverse=True)")
print("=" * 80)
if result and "result" in result:
    for item in result["result"].get("content", []):
        print(item.get("text", ""))
elif result and "error" in result:
    print("ERROR:", json.dumps(result["error"], indent=2))
else:
    print("No result")

proc.terminate()
