import subprocess
import json
import sys

proc = subprocess.Popen(
    ['uvx', 'regen-python-mcp'],
    stdin=subprocess.PIPE,
    stdout=subprocess.PIPE,
    stderr=subprocess.PIPE,
    env={"PATH": "/home/ygg/.local/bin:/usr/local/bin:/usr/bin:/bin", "HOME": "/home/ygg", "REGEN_MCP_LOG_LEVEL": "WARNING"}
)

def send_rpc(proc, method, params=None, id_num=1):
    msg = {"jsonrpc": "2.0", "method": method, "id": id_num}
    if params is not None:
        msg["params"] = params
    data = json.dumps(msg) + "\n"
    proc.stdin.write(data.encode())
    proc.stdin.flush()
    line = proc.stdout.readline().decode().strip()
    return json.loads(line) if line else None

# Initialize MCP connection
send_rpc(proc, "initialize", {
    "protocolVersion": "2024-11-05",
    "capabilities": {},
    "clientInfo": {"name": "test", "version": "1.0"}
}, 1)
notif = {"jsonrpc": "2.0", "method": "notifications/initialized"}
proc.stdin.write((json.dumps(notif) + "\n").encode())
proc.stdin.flush()

# Paginate through all sell orders
offset = 0
page_num = 0
rpc_id = 2

while True:
    page_num += 1
    print(f"--- Fetching page {page_num} (offset={offset}, limit=1000) ---", file=sys.stderr)

    result = send_rpc(proc, "tools/call", {
        "name": "list_sell_orders",
        "arguments": {"limit": 1000, "count_total": True, "offset": offset}
    }, rpc_id)
    rpc_id += 1

    if result and "result" in result:
        for item in result["result"].get("content", []):
            text = item.get("text", "")
            print(text)

        # Try to parse the response to check for pagination
        try:
            for item in result["result"].get("content", []):
                text = item.get("text", "")
                data = json.loads(text)
                sell_orders = data.get("sell_orders", [])
                pagination = data.get("pagination", {})
                total = pagination.get("total")
                next_key = pagination.get("next_key")

                print(f"--- Got {len(sell_orders)} orders, total={total}, next_key={next_key} ---", file=sys.stderr)

                if not next_key or len(sell_orders) == 0:
                    proc.terminate()
                    sys.exit(0)

                offset += len(sell_orders)
        except (json.JSONDecodeError, TypeError):
            print("--- Could not parse pagination, stopping ---", file=sys.stderr)
            break
    elif result and "error" in result:
        print("ERROR:", json.dumps(result["error"], indent=2))
        break
    else:
        print("No result received")
        break

proc.terminate()

